    temp_manifest.entry_point = 11;
    temp_manifest.signature = 12;
    temp_manifest.pub_signature_key.key = 13;

    temp_manifests.rom_exts_mfs[0] = temp_manifest;
    temp_manifests.size = 1;

    rom_exts_manifests = temp_manifests; 
}

