        GlobalMemory[i] = memory[i + startIndex];
    }
}
*/
void writeMemory(int startIndex, int endIndex){
    int i;
    for(i = 0; i <= endIndex - startIndex; i++){
        memory[i + startIndex] = GlobalMemory[i];
    }
}




