typedef struct {
    int[0,2] identifier; // 0 -> missing, 1 -> contains magic value, 2 -> does not contain magic value
    int entryPoint; //address in memory
    pub_key_t publicKey; // The public key used to verify the signature of the manifest
    int[0,2] signature; // 0 -> missing, 1 -> correct signature, 2 -> bad signature
    //pub_key_t pub_signature_key;
    int[0,2] image_code; // 0 -> missing, 1 -> correct image code, 2 -> hashed image code
} rom_ext_manifest_t;

