//Is not called atm 16-04-2021
void readSRAM(int e){
    if(e == 0){
        GlobalBootPolicy = bootPolicy;
    } else if (e == 1) {
        GlobalDigest = digest;
    } else if (e == 2) {
        GlobalImageCode = imageCode;
    } else if (e == 3) 
        GlobalManifests = manifests;
      else if (e == 4)
        GlobalPublicKey = publicKey;

}


void writeSRAM(){
    SRAM[SRAMIndex] = GlobalSecretKey;
    GlobalSecretKey = 0;
    SRAMIndex = (SRAMIndex + 1) % SRAMsize;
}
