bool EqualManifestContents(rom_ext_manifest_t manifest1, rom_ext_manifest_t manifest2){
    return manifest1.identifier == manifest2.identifier &&
           manifest1.publicKey.exponent == manifest2.publicKey.exponent &&
           manifest1.publicKey.modulus == manifest2.publicKey.modulus &&
           manifest1.image_code == manifest2.image_code &&
           manifest1.signature == manifest2.signature;
}

