}
bool canRead(int startIndex, int endIndex){
    int i;
    for(i = 0; i < 16;i++)
        if (PmpRegions[i].startAddress <= startIndex && PmpRegions[i].endAddress >= endIndex)
            if (PmpRegions[i].read)
                return true;
    return false;
}

